mapper.select:
SELECT o.order_id,o.actual_pay, o.total_pay,o.create_time, os.status, od.sku_id, od.title ,od.image, od.num , od.own_spec, od.price FROM tb_order o LEFT JOIN tb_order_status os ON os.order_id = o.order_id LEFT JOIN tb_order_detail od ON od.order_id = o.order_id WHERE o.user_id = #{userId}
AND os.status = #{status}
ORDER BY o.create_time DESC

SELECT DISTINCT o.order_id,o.actual_pay,o.buyer_nick, o.total_pay,o.create_time, os.status FROM tb_order o LEFT JOIN tb_order_status os ON os.order_id = o.order_id LEFT JOIN tb_order_detail od ON od.order_id = o.order_id WHERE 1=1
AND os.status = #{status}
ORDER BY o.create_time DESC /* where os.status = 2*/

SELECT SUM(num) FROM tb_order_detail tod
LEFT JOIN tb_order_status tos ON tos.order_id=tod.order_id
WHERE tos.status in (2,3,4) AND tod.sku_id=#{skuId}
